import { React, useState } from 'react';

function SearchOptions({ props }) {
    const [isOptionShown, setOptionShown] = useState(false);

    const showOptions = () => {
        setOptionShown(!isOptionShown);

    };

    const updateQuery = (e) => {
        const {name, checked} = e.target;
        setQuery({...prop.query, [name]: checked})
        // console.log(query.courseCode == "true")
    };

    return (
        <div>
            {isOptionShown && (
            <div className="m-2">
                <label className="m-4">
                    <input className="m-2" type="checkbox" name="courseCode" onChange={ updateQuery } checked={ query.courseCode }/>
                    Course Code
                </label>
                <label className="m-4">
                    <input className="m-2" type="checkbox" name="courseTitle" onChange={ updateQuery } checked={ query.courseTitle }/>
                    Course Title
                </label>
                <label className="m-4">
                    <input className="m-2" type="checkbox" name="creditUnitLec" onChange={ updateQuery } checked={ query.creditUnitLec }/>
                    Credit Unit (LEC)
                </label>
                <label className="m-4">
                    <input className="m-2" type="checkbox" name="creditUnitLab" onChange={ updateQuery } checked={ query.creditUnitLab }/>
                    Credit Unit (LAB)
                </label>
                <label className="m-4">
                    <input className="m-2" type="checkbox" name="contactHrsLec" onChange={ updateQuery } checked={ query.contactHrsLec }/>
                    Contact Hrs (LEC)
                </label>
                <label className="m-4">
                    <input className="m-2" type="checkbox" name="contactHrsLab" onChange={ updateQuery } checked={ query.courseCode }/>
                    Contact Hrs (LAB)
                </label>
                <label className="m-4">
                    <input className="m-2" type="checkbox" name="prerequisite" onChange={ updateQuery } checked={ query.prerequisite }/>
                    Pre-requisite
                </label>
                <label className="m-4">
                    <input className="m-2" type="checkbox" name="finalGrade" onChange={ updateQuery } checked={ query.finalGrade }/>
                    Final Grade
                </label>
                <label className="m-4">
                    <input className="m-2" type="checkbox" name="instructorName" onChange={ updateQuery } checked={ query.instructorName }/>
                    Instructor Name
            </label></div>)}
        </div>
    );
}

export default SearchOptions;
