import { React, useEffect} from 'react';

function SearchBar(props) {
    const showOptions = () => {
        props.setOptionShown(!props.isOptionShown);
    };

    const handleKeyDown = (event) => {
        if (event.key === 'Backspace') {
            const searchQuery = "";
            props.setQuery({...props.query, searchQuery});
        }
    };

    const handleInputChange = (e) => {
        const searchQuery = e.target.value;
        props.setQuery(() => ({...props.query, searchQuery}));
        
    };

    useEffect(() => {
        props.onSearch();
    }, [props.query]);
    
    return (
        <div className="overlay bg-black h-20 flex justify-center p-4">
            <div className="bg-gray-600 w-2/5 flex rounded-xl border-gray-400 overflow-hidden">
                <input className="block w-full pl-4 rounded-l-xl bg-transparent text-gray-300 placeholder-gray-300 border-l border-y border-gray-400 focus:ring-2 focus:ring-inset focus:ring-gray-400" type="text" placeholder="Search" onKeyDown={ handleKeyDown } onChange={ handleInputChange }/>
                <button className="px-4 py-2 bg-gray-600 relative z-10 rounded-r-xl border text-gray-300 border-gray-400 hover:bg-gray-400 hover:text-gray-100" onClick={ showOptions }>
                    {props.isOptionShown? "▼":"▲"}
                </button>
            </div>
        </div>
    );
}

export default SearchBar;
