import { React, useState, useEffect} from 'react';

function SearchBar(props) {
    const showOptions = () => {
        props.setOptionShown(!isOptionShown);
    };

    const handleKeyDown = (event) => {
        if (event.key === 'Backspace') {
            const searchQuery = "";
            props.setQuery({...props.query, searchQuery});
        }
    };

    const handleInputChange = (e) => {
        const searchQuery = e.target.value;
        setQuery(query => ({...props.query, searchQuery}));
        
    };

    useEffect(() => {
        onSearch();
    }, [query]);
    
    return (
        <div>
            <button className="px-4 py-2 bg-gray-300 hover:bg-gray-400" onClick={ showOptions }>{isOptionShown? "▼":"▲"}</button>
            <input type="text" placeholder="Search" onKeyDown={ handleKeyDown } onChange={ handleInputChange }></input>
        </div>
    );
}

export default SearchBar;
