import { React, useState } from 'react';

function SearchOptions({ query, setQuery }) {
    const [isOptionShown, setOptionShown] = useState(false);

    const showOptions = () => {
        setOptionShown(!isOptionShown);

    };

    const updateQuery = (e) => {
        const {name, checked} = e.target;
        setQuery({...query, [name]: checked})
        // console.log(query.courseCode == "true")
    };

    return (
        <div>
            <button className="px-4 py-2 bg-gray-300 hover:bg-gray-400" onClick={ showOptions }>{isOptionShown? "▼":"▲"}</button>
            {isOptionShown && (<>
            <label>
                <input type="checkbox" name="courseCode" onChange={ updateQuery } checked={ query.courseCode }/>
                Course Code
            </label>
            <label>
                <input type="checkbox" name="courseTitle" onChange={ updateQuery } checked={ query.courseCode }/>
                Course Title
            </label>
            <label>
                <input type="checkbox" name="creditUnitLec" onChange={ updateQuery }/>
                Credit Unit (LEC)
            </label>
            <label>
                <input type="checkbox" name="creditUnitLab" onChange={ updateQuery }/>
                Credit Unit (LAB)
            </label>
            <label>
                <input type="checkbox" name="contactHrsLec" onChange={ updateQuery }/>
                Contact Hrs (LEC)
            </label>
            <label>
                <input type="checkbox" name="contactHrsLab" onChange={ updateQuery }/>
                Contact Hrs (LAB)
            </label>
            <label>
                <input type="checkbox" name="prerequisite" onChange={ updateQuery }/>
                Pre-requisite
            </label>
            <label>
                <input type="checkbox" name="finalGrade" onChange={ updateQuery }/>
                Final Grade
            </label>
            <label>
                <input type="checkbox" name="instructorName" onChange={ updateQuery }/>
                Instructor Name
            </label></>)}
        </div>
    );
}

export default SearchOptions;
