import React from 'react'
import {useRef, useEffect, useState} from 'react'
import axios from 'axios'
import SearchBar from './components/SearchBar'
import Records from './components/Records'
import SearchOptions from './components/SearchOptions'
import '../index.css';
const Homepage = () => {
    const ref = useRef(null);
    const [records, setRecords] = useState([]);
    const [isOptionShown, setOptionShown] = useState(false);
    const [query, setQuery] = useState({
        searchQuery: '',
        courseCode: false,
        courseTitle: false,
        creditUnitLec: false,
        creditUnitLab: false,
        contactHrsLec: false,
        contactHrsLab: false,
        prerequisite: false,
        finalGrade: false,
        instructorName: false
    });
    

    const handleSearch = () => {
        fetchAllRecords();
    };

    const fetchAllRecords = async () => {
        try {
            const res = await axios.get("http://localhost:8800/student_records", {params: query})
            // console.log(query)
            // console.log(res.data)
            setRecords(res.data);
        } catch(err) {
            console.log(err);
        }
    }

    useEffect(() => {
        fetchAllRecords();
    }, [query]);

    return  <div>
        <div className="max-h-screen overflow-hidden">
            <SearchBar onSearch={ handleSearch } query={ query } setQuery={ setQuery }  isOptionShown={ isOptionShown } setOptionShown={ setOptionShown }/>
            <SearchOptions query={ query } setQuery={ setQuery } isOptionShown={ isOptionShown } setOptionShown={ setOptionShown }/>
            <Records records={ records }/>
        </div>
    </div>;
}

export default Homepage
