import React from 'react'
import {useEffect, useState} from 'react'
import axios from 'axios'
import SearchBar from './Component/SearchBar'
import Records from './Component/Records'
import SearchOptions from './Component/SearchOptions'

const Homepage = () => {
    const [records, setRecords] = useState([]);
    const [query, setQuery] = useState({});

    const handleSearch = () => {
        fetchAllRecords();
    };

    const fetchAllRecords = async () => {
        try {
            const res = await axios.get("http://localhost:8800/student_records", {params: query})
            console.log(query)
            console.log(res.data)
            setRecords(res.data);
        } catch(err) {
            console.log(err);
        }
    }

    useEffect(() => {
        fetchAllRecords();
    }, [query]);

    return  <div>
        <div className="chat-notification">
            <div className="chat-notification-content">
                <h4 className="chat-notification-title">ChitChat</h4>
                <p className="chat-notification-message">You have a new message!</p>
            </div>
        </div>

        <SearchBar onSearch={ handleSearch } query={ query } setQuery={ setQuery }/>
        <SearchOptions query={ query } setQuery={ setQuery }/>
        <Records records={ records }/>
    </div>;
}

export default Homepage
