import React from 'react';

function SearchBar({ onSearch, query, setQuery }) {
    const handleKeyDown = (event) => {
        // Check if Ctrl+A is pressed
        if ((event.ctrlKey || event.metaKey) && event.key === 'a') {
            console.log('Ctrl+A is pressed');
            event.preventDefault();  // Prevents Ctrl+A select all
        }

        // Check if Delete is pressed
        if (event.key === 'Delete') {
            console.log('Delete key is pressed');
            event.preventDefault();  // Prevents the delete action
        }
    };

    
    const handleInputChange = (e) => {
        const searchQuery = e.target.value;
        setQuery({...query, searchQuery})
        onSearch();
    };
    
    return (
        <div>
            <input type="text" placeholder="Search" onChange={ handleInputChange }></input>
        </div>
    );
}

export default SearchBar;
