import React from 'react'
import {useEffect, useState} from 'react'
import axios from 'axios'
import SearchBar from './Component/SearchBar'
import Records from './Component/Records'
import SearchOptoins from './Component/Search'

const Homepage = () => {
    const [records, setRecords] = useState([]);
    const [dataShown, setDataShown] = useState([]);

    const handleSearch = (query) => {
        const filteredData = records.filter((record) =>
                Object.values(record).some((value) =>
                String(value).toLowerCase().includes(query.toLowerCase())
            )
        );
        setDataShown(filteredData);
    };
    
    useEffect(() => {
        console.log("Connected to client.")
        const fetchAllRecords = async () => {
            try {
                console.log("tried")
                const res = await axios.get("http://localhost:8800/student_records")
                setRecords(res.data);
                setDataShown(res.data);
            } catch(err) {
                console.log(err);
            }
        }
        fetchAllRecords();
    }, []);

    return  <div>
        <SearchBar onSearch={ handleSearch }/>
        <SearchOptions/>
        <Records dataShown={ dataShown }/>
    </div>;
}

export default Homepage
