import React from 'react'
import {useEffect, useState} from 'react'
import axios from 'axios'
import SearchBar from '../Components/SearchBar'

const Records = () => {
    const [records, setRecords] = useState([]);

    useEffect(() => {
        console.log("Connected to client.")
        const fetchAllRecords = async () => {
            try {
                console.log("tried")
                const res = await axios.get("http://localhost:8800/student_records")
                setRecords(res.data);
            } catch(err) {
                console.log(err);
            }
        }
        fetchAllRecords();
    }, []);

    const handleSearch = (query) => {
        setRecords(records.filter((record) =>
                Object.values(record).some((value) =>
                String(value).toLowerCase().includes(query.toLowerCase())
            )
        ))
        
    };

    return  <div>
        <SearchBar onSearch={ handleSearch }/>
        <table>
            <thead>
                <tr>
                    <th scope="col">Course Code</th>
                    <th scope="col">Course Title</th>
                    <th scope="col">Credit Unit (LEC)</th>
                    <th scope="col">Credit Unit (LAB)</th>
                    <th scope="col">Contact Hrs (LEC)</th>
                    <th scope="col">Contact Hrs (LAB)</th>
                    <th scope="col">Pre-requisite</th>
                    <th scope="col">Sem / Yr taken</th>
                    <th scope="col">Final Grade</th>
                    <th scope="col">Instructor</th>
                </tr>
            </thead>
            <tbody>
                {records.map(record => (
                    <tr key={record.course_code}>
                    <td>{record.course_code}</td> 
                    <td>{record.course_title}</td>
                    <td>{record.credit_unit_lec}</td>
                    <td>{record.credit_unit_lab}</td>
                    <td>{record.contact_hrs_lec}</td>
                    <td>{record.conact_hrs_lab}</td>
                    <td>{record.prerequisite}</td>      
                    <td>Year {record.year_taken} Sem {record.sem_taken}</td>
                    <td>{record.final_grade}</td>
                    <td>{record.instructor_name}</td>  
                    </tr>
                ))}
            </tbody>
        </table>
    </div>;
}

export default Records
