import { React, useState, useEffect} from 'react';

function SearchBar(props) {
    const [isOpen, setIsOpen] = useState(false);

    // Toggle dropdown open/close
    const toggleDropdown = () => setIsOpen(!isOpen);
    const showOptions = () => {
        props.setOptionShown(!props.isOptionShown);
    };

    const handleKeyDown = (event) => {
        if (event.key === 'Backspace') {
            const searchQuery = "";
            props.setQuery({...props.query, searchQuery});
        }
    };

    const handleInputChange = (e) => {
        const searchQuery = e.target.value;
        props.setQuery(() => ({...props.query, searchQuery}));
        
    };

    useEffect(() => {
        props.onSearch();
    }, [props.query]);
    
    return (
        <div className="overflow-auto  h-20 flex justify-center p-4">
            
            <button onClick={toggleDropdown} className="px-4 py-2 bg-blue-500 text-white rounded-md focus:outline-none">
                    Dropdown
            </button>
                {isOpen && (
                    <ul className="absolute bg-white shadow-md w-48 mt-2 rounded-md overflow-hidden">
                    <li><a href="#" className="block px-4 py-2 hover:bg-gray-100 text-gray-700">Action 1</a></li>
                    <li><a href="#" className="block px-4 py-2 hover:bg-gray-100 text-gray-700">Action 2</a></li>
                    <li><a href="#" className="block px-4 py-2 hover:bg-gray-100 text-gray-700">Action 3</a></li>
                    </ul>
      )}
            <div className="bg-gray-600 w-3/5 grid grid-cols-10 rounded-2xl border-gray-400 overflow-hidden">
                <div className="grid grid-cols-2 col-span-3 bg-cyan-100">
                    <button className="w-full px-4 py-2 bg-gray-600 relative z-10 rounded-l-2xl border text-gray-300 border-gray-400 hover:bg-gray-400 hover:text-gray-100" type="dropdown" onClick={ showOptions }>
                        <p>1st Year</p><p>▼</p>
                    </button>
                    <button className="w-full px-4 py-2 bg-gray-600 relative z-10 border text-gray-300 border-gray-400 hover:bg-gray-400 hover:text-gray-100" onClick={ showOptions }>
                        {props.isOptionShown? "▼":"▲"}
                    </button>

                 
                </div>
                <div className="flex col-span-7">
                    <input className="block w-full pl-4 bg-transparent text-gray-300 placeholder-gray-300 border-l border-y border-gray-400 focus:ring-1 focus:outline-none focus:ring-inset focus:ring-gray-300" type="text" placeholder="Search" onKeyDown={ handleKeyDown } onChange={ handleInputChange }/>
                    <button className="px-4 py-2 bg-gray-600 relative z-10 rounded-r-2xl border text-gray-300 border-gray-400 hover:bg-gray-400 hover:text-gray-100" onClick={ showOptions }>
                        {props.isOptionShown? "▼":"▲"}
                    </button>
                </div>
            </div>
        </div>
    );
}

export default SearchBar;
