import { React, useState, useEffect} from 'react';

function SearchBar(props) {
    const showOptions = () => {
        props.setOptionShown(!props.isOptionShown);
    };

    const handleKeyDown = (event) => {
        if (event.key === 'Backspace') {
            const searchQuery = "";
            props.setQuery({...props.query, searchQuery});
        }
    };

    const handleInputChange = (e) => {
        const searchQuery = e.target.value;
        props.setQuery(() => ({...props.query, searchQuery}));
        
    };

    useEffect(() => {
        props.onSearch();
    }, [props.query]);
    
    return (
        <div  className="bg-black flex justify-center p-4">
            <div className="bg-gray-500 flex flex-wrap items-stretch rounded-xl border-2 border-gray-400 overflow-hidden">
                <input className="pl-4 rounded-l-xl bg-transparent text-black placeholder-gray-700" type="text" placeholder="Search" onKeyDown={ handleKeyDown } onChange={ handleInputChange }/>
                <button className="px-4 py-2 bg-transparent relative z-10 hover:bg-gray-600 hover:text-white" onClick={ showOptions }>
                    {props.isOptionShown? "▼":"▲"}
                </button>
            </div>
        </div>
    );
}

export default SearchBar;
